/**
 * TableGroup Component
 * Renders a single group of SVG elements representing a table and its chairs,
 * and handles mouse events for hover interactivity.
 */
const TableGroup = ({ table, onMouseEnter, onMouseLeave }) => {
  const groupRef = useRef(null); // Ref to get the bounding box of the SVG group

  // Handles mouse entering the table group, passing its ID and bounding rectangle
  const handleMouseEnter = () => {
    if (groupRef.current) {
      const rect = groupRef.current.getBoundingClientRect();
      onMouseEnter(table.id, rect); // Pass the table ID and its bounding client rect
    }
  };

  return (
    <g
      ref={groupRef}
      id={table.id}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={onMouseLeave}
      className="cursor-pointer transition-all duration-200 hover:opacity-75" // Tailwind classes for hover effect
    >
      {table.svgElements} {/* Render the SVG elements associated with this table */}
    </g>
  );
};

/**
 * App Component
 * Main component that renders the SVG map and manages the hover state for tooltips.
 */
export default function Test() {
  const [hoveredTableId, setHoveredTableId] = useState(null); // State to track which table is hovered
  const [tooltipPosition, setTooltipPosition] = useState({ x: 0, y: 0 }); // State for tooltip position

  // Callback for when mouse enters a table group
  const handleMouseEnter = (id, rect) => {
    setHoveredTableId(id);
    // Set tooltip position relative to the viewport, centered above the hovered element
    setTooltipPosition({
      x: rect.left + rect.width / 2, // Center horizontally
      y: rect.top, // Align to the top of the element
    });
  };

  // Callback for when mouse leaves a table group
  const handleMouseLeave = () => {
    setHoveredTableId(null);
  };

  // Find the currently hovered table data to display in the tooltip
  const currentTable = tablesData.find((table) => table.id === hoveredTableId);

  return (
    // Main container for the application, centered and responsive
    <div className="relative flex items-center justify-center min-h-screen bg-gray-100 p-4 font-inter">
      {/* White card container for the layout */}
      <div className="bg-white rounded-lg shadow-xl p-6 w-full max-w-2xl">
        <h1 className="text-2xl font-bold text-gray-800 mb-4 text-center">Restaurant Layout</h1>
        {/* SVG container, responsive and centered */}
        <div className="relative w-full h-auto flex justify-center items-center">
          <svg
            version="1.1"
            id="Layer_1_svg"
            xmlns="http://www.w3.org/2000/svg"
            xmlnsXlink="http://www.w3.org/1999/xlink" // Corrected attribute name
            x="0px"
            y="0px"
            viewBox="0 0 11.12 25.31" // Original SVG viewBox
            className="w-full h-full max-w-xl" // Tailwind classes for responsive scaling
            style={{ enableBackground: 'new 0 0 11.12 25.31' }} // Corrected attribute name
          >
            {/* Embedded SVG styles */}
            <style type="text/css">
              {`
                .st0{fill:#085C5E;} /* Dark Teal - Tables/Main structures */
                .st1{fill:#367F33;} /* Green - Chairs */
                .st2{fill:#4A494A;} /* Dark Grey - Walls/Structural */
                .st3{fill:#E62F1F;} /* Red */
                .st4{fill:#F39200;} /* Orange */
              `}
            </style>

            {/* Render each interactive table group */}
            {tablesData.map((table) => (
              <TableGroup
                key={table.id}
                table={table}
                onMouseEnter={handleMouseEnter}
                onMouseLeave={handleMouseLeave}
              />
            ))}

            {/* Render static SVG elements (walls, structural parts) */}
            {staticSvgElements}
          </svg>

          {/* Tooltip for hovered tables */}
          {hoveredTableId && currentTable && (
            <div
              className="fixed bg-gray-800 text-white text-sm p-2 rounded-lg shadow-lg pointer-events-none z-50 transition-opacity duration-300"
              style={{
                left: tooltipPosition.x,
                top: tooltipPosition.y,
                transform: 'translate(-50%, -110%)', // Centers horizontally and positions above
                opacity: hoveredTableId ? 1 : 0, // Controls visibility
                whiteSpace: 'nowrap', // Prevents text wrapping
              }}
            >
              <h3 className="font-bold text-lg mb-1">{currentTable.name}</h3>
              <p>Status: <span className={`${currentTable.status === 'Available' ? 'text-green-400' : 'text-red-400'}`}>{currentTable.status}</span></p>
              <p>Capacity: {currentTable.capacity}</p>
              <p>{currentTable.description}</p>
              {/* Small triangle pointer for the tooltip */}
              <div className="absolute w-3 h-3 bg-gray-800 transform rotate-45 -translate-x-1/2 -bottom-1 left-1/2"></div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}


